/* Bar color */
#page_header_cont {
    background-color: LightSteelBlue !important
}
/* Bar buttons & text */
.top_profile_arrow,
.top_nav_btn_icon,
.top_audio_player_btn_icon {
    filter: brightness(0%)
}
.top_audio_player_title {
    color: Black !important
}

/* Profile page */
#l_pr a div svg {
    filter: brightness(0) saturate(100%) invert(52%) sepia(61%) saturate(482%) hue-rotate(252deg) brightness(96%) contrast(91%);
}
